//判断element是否为arr数组中的元素,是则返回true,否则返回false
bool isElements(int arr[], int element, int count)
{
	int i;
	for (i = 0; i < count; i++)
	{
		if (arr[i] == element)
			return true;
	}
	return false;
}

int sumOfSingleElements(int arr[], int n)
{
	/* record指向的是下一个要查找的元素 */
	int* record = arr;
	int sum = 0;
	int* Arr = (int*)malloc(n * sizeof(int));
	int count = 0; //记录Count中的元素个数 - 1
	Arr[count] = arr[0];
	count++;
	//比较元素
	
	int i;
	for (i = 0; i < n; i++)
	{
		//判断
		if (i != 0 == true && isElements(Arr, *record, count))
		{
			record++;
			continue;
		}
		int j;
		for (j = 0; j < n - i - 1; j++)
		{
			if (*record == arr[i + j + 1])
			{
				Arr[count] = *record;
				count++;
				record++;
				break;
			}
		}
		//判断循环是因为什么条件而终止
		if (j == n - i - 1)
		{
			Arr[count] = *record;
			count++;
			sum += *record;
			record++;
		}

	}

	return sum;
}